return new com.sugarcrm.sugarcrm.GetEntryRequestType(
	session: body.return.id,
	moduleName: "Accounts",
	id: request.headers['id'],
	selectFields: new com.sugarcrm.sugarcrm.SelectFields[0]
)

def nameValues = new XmlSlurper().parseText(body).return.entry_list.item[0].name_value_list;
def contactMediumList = new ArrayList<com.tmf.cim.customer.ContactMedium>();
def contactMedium = new com.tmf.cim.customer.ContactMedium(
	mediumType: 'billingAddress',
	characteristic: new com.tmf.cim.customer.MediumCharacteristic(
		street1: nameValues.'*'.find{node -> node.name()=='item' && node.name=='billing_address_street'}.value,
		postCode: nameValues.'*'.find{node -> node.name()=='item' && node.name=='billing_address_postalcode'}.value,
		city: nameValues.'*'.find{node -> node.name()=='item' && node.name=='billing_address_city'}.value,
		country: nameValues.'*'.find{node -> node.name()=='item' && node.name=='billing_address_country'}.value
	)
)

contactMediumList.add(contactMedium);

return new com.tmf.cim.customer.Customer(
	id: request.headers['id'],
	name: nameValues.'*'.find{node -> node.name()=='item' && node.name=='name'}.value,
	contactMedium: contactMediumList
)

    def customer = exchange.properties['customer'];
	def nameValueList = new com.sugarcrm.sugarcrm.NameValueList();

	nameValueList.arrayType = "tns:name_value[5]";

	nameValueList.getAny().add(new javax.xml.bind.JAXBElement<com.sugarcrm.sugarcrm.NameValue>(new javax.xml.namespace.QName("name_value"), com.sugarcrm.sugarcrm.NameValue.class, new com.sugarcrm.sugarcrm.NameValue(name: 'name', value: customer.name)));
	nameValueList.getAny().add(new javax.xml.bind.JAXBElement<com.sugarcrm.sugarcrm.NameValue>(new javax.xml.namespace.QName("name_value"), com.sugarcrm.sugarcrm.NameValue.class, new com.sugarcrm.sugarcrm.NameValue(name: 'billing_address_street', value: customer.contactMedium[0].characteristic.street1)));
	nameValueList.getAny().add(new javax.xml.bind.JAXBElement<com.sugarcrm.sugarcrm.NameValue>(new javax.xml.namespace.QName("name_value"), com.sugarcrm.sugarcrm.NameValue.class, new com.sugarcrm.sugarcrm.NameValue(name: 'billing_address_postalcode', value: customer.contactMedium[0].characteristic.postCode)));
	nameValueList.getAny().add(new javax.xml.bind.JAXBElement<com.sugarcrm.sugarcrm.NameValue>(new javax.xml.namespace.QName("name_value"), com.sugarcrm.sugarcrm.NameValue.class, new com.sugarcrm.sugarcrm.NameValue(name: 'billing_address_city', value: customer.contactMedium[0].characteristic.city)));
	nameValueList.getAny().add(new javax.xml.bind.JAXBElement<com.sugarcrm.sugarcrm.NameValue>(new javax.xml.namespace.QName("name_value"), com.sugarcrm.sugarcrm.NameValue.class, new com.sugarcrm.sugarcrm.NameValue(name: 'billing_address_country', value: customer.contactMedium[0].characteristic.country)));

	return new com.sugarcrm.sugarcrm.SetEntryRequestType(
		session: exchange.properties['sessionId'],
		moduleName: "Accounts",
		nameValueList: nameValueList
	)

